-- 选择职业
_G.sel_job = function()
    if actor_unit.get_class_preview_canvas() ~= 0 then
        actor_unit.preview_class(0xC9 + 4)
        decider.sleep(5000)
        if not local_player:is_move() then
            common.auto_move(11838.00,153.00,14.00)
        end
        decider.sleep(5000)
        actor_unit.exit_prieview_class()
        decider.sleep(5000)
        actor_unit.select_class(0xC9 + 4)
        decider.sleep(5000)
        if not local_player:is_move() then
            common.auto_move(11846.00,158.00,14.00)
        end
    end
end

