-- 跳过序幕
_G.out_prologue = function()
    -- 是否在序幕中
    if ui_unit.is_in_prologue() then
        ui_unit.click_exit_prologue()
        decider.sleep(5000)
    end
    if ui_unit.is_in_prologue_skip() then
        ui_unit.confirm_exit_prologue()
        decider.sleep(5000)
    end
end

