                local idx = string.format('%0.0f', task_info.idx)
                local branch_name = ''
                local branch_info = task_info.branch_info
                for _, v1 in pairs(branch_info) do
                    if v1.cur_tar_num < v1.cur_tar_max_num then
                        branch_name = v1.branch_name
                        break
                    end
                end
                for k, v in pairs(quest) do
                    if k == branch_name or k == branch_name .. idx then
                        xxmsg('执行任务' .. branch_name)
                        local r = 300
                        local path_num = v.path_num
                        local path = {}
                        for i = 1, path_num do
                            path[i] = pos_path[i]
                        end
                        if not table.is_empty(path) then
                            map_ent.execute_move_by_path(path, v.move_to.x, v.move_to.y, v.move_to.z, v.move_to.r)
                            local kill_dist = v.monster or path[path_num]['击杀']
                            local attack    = false
                            local max_dis   = v.move_to.r or 800
                            if not table.is_empty(kill_dist) then
                                attack = map_ent.move_to_kill_actor(kill_dist,max_dis)
                            end
                            if v.move_to and not attack then
                                common.auto_move_ex(v.move_to.x,v.move_to.y, v.move_to.z)
                            end
                        end
                        if v.gather then
                            local move_to = v.move_to
                            if not table.is_empty(move_to) then
                                if local_player:dist_xy(move_to.x,move_to.y) > move_to.r then
                                    if not common.is_move() then
                                        common.auto_move(move_to.x,move_to.y,move_to.z)
                                    end
                                else
                                    quest_ent.go_gather_by_info('剩下的力量', branch_name, v)
                                end
                            end
                        end
                    end
                end
            end
        else
            break
        end
        decider.sleep(100)
    end
end

