            -- xxmsg('薄暮之雾')
            ui_ent.esc_cinema()
            local task_info = quest_ent.get_quest_info_by_task_name('守魂的怪物')
            if table.is_empty(task_info) then
                music_ent.use_music_by_name('逃离之歌')
                return
            else
                local idx = string.format('%0.0f', task_info.idx)
                local branch_name = ''
                local branch_info = task_info.branch_info
                for _, v1 in pairs(branch_info) do
                    if v1.cur_tar_num < v1.cur_tar_max_num then
                        branch_name = v1.branch_name
                        break
                    end
                end
                for k, v in pairs(quest) do
                    if k == branch_name or k == branch_name .. idx then
                        xxmsg('执行任务' .. branch_name)
                        local r = 300
                        local path_num = v.path_num
                        local path = {}
                        for i = 1, path_num do
                            path[i] = pos_path[i]
                        end
                        if not table.is_empty(path) then
                            local check_task_func = { func = quest_ent.is_over_task,task_name = '守魂的怪物',branch_name = branch_name,quest_idx = task_info.idx }
                            map_ent.execute_move_by_path(pos_path, v.move_to.x, v.move_to.y, v.move_to.z, r, nil, check_task_func)
                            local kill = { { name = '堕落居士', type = 2 } }
                            map_ent.move_to_kill_actor(kill,500)
                            local kill_dist = v.monster
                            local attack = false
                            if not table.is_empty(kill_dist) then
                                attack = map_ent.move_to_kill_actor(kill_dist,800)
                            end
                            if v.move_to and not attack then
                                common.auto_move_ex(v.move_to.x,v.move_to.y, v.move_to.z)
                            end
                        end
                        if not table.is_empty(v.gather) then
                            -- xxmsg('采集守魂的怪物')
                            quest_ent.go_gather_by_info('守魂的怪物', branch_name, v)
                        end
                    end
                end
            end
        else
            -- xxmsg('111')
            break
        end
        decider.sleep(100)
    end
end

