-- [条件] 目标任务是否必须执行
quest_res.is_must_to_do_task = function(task_name)
    -- 必须执行的任务[日常]
    local info = daily_quest_res.EVERY_DAY_QUEST[task_name]
    if info and info.is_open then
        return true
    end
    -- 必须执行的任务[其他:岛屿 等相关任务]
    info = other_task_res.MUST_DO_TASK[task_name]
    if info then
        -- 是否战力限制
        local need_power = info.need_power
        if type(need_power) == 'number' then
            if item_unit.get_equip_prop_level() < need_power then
                return false
            end
        end
        return true
    end
    -- 返回不可执行
    return false
end

-------------------------------------------------------------------------------------
-- [条件] 目标任务是否为断档
quest_res.is_need_break_task = function(task_name)
    local info = this.QUEST_NEED_POWER[task_name]
    if info then
        if item_unit.get_equip_prop_level() < info then
            return true
        end
    end
    return false
end


-------------------------------------------------------------------------------------
-- 获取最佳的分支名 与 配对值
quest_res.get_best_bran_name = function(task_name,branch_name,quest_idx)
    local task_info = this.QUEST_INFO[task_name]
    quest_idx       = quest_idx and math.floor(quest_idx) or -1
    local new_list  = {}
    if not table.is_empty(task_info) then
        if  task_info['全局执行'] and task_info['全局执行'] ~= '' then
            return ''
        end
    end
    if not task_info or task_info and ( not task_info[branch_name] and not task_info[branch_name..quest_idx] ) then
        if not table.is_empty(task_info) then
            for name,v in pairs(task_info) do
                if type(v) == 'table' then
                    local value = common.calculatematch(branch_name, name)
                    -- xxmsg(name..' '..branch_name..' '..value)
                    table.insert(new_list,{ branch_name = name,value = value })
                end
            end
        end
    end
    if not table.is_empty(new_list) then
        table.sort(new_list,function(a, b) return a.value > b.value end)
        if new_list[1].value >= 70 then
            return new_list[1].branch_name
        end
    end
    return ''
end

-------------------------------------------------------------------------------------
-- [读取] 获取分支名称[配对异常]
quest_res.get_bran_name = function(quest_name,task_id,main_idx,bran_idx)
    local info = this.QUEST_INFO[quest_name]
    if not table.is_empty(info) then
        for branch_name,v in pairs(info) do
            if type(v) == 'table' then
                -- xxmsg(quest_name..' '..tostring(v.task_key)..' '..math.floor(task_id)..math.floor(main_idx)..math.floor(bran_idx))
                if v.task_key == tonumber(math.floor(task_id)..math.floor(main_idx)..math.floor(bran_idx)) then
                    return branch_name
                end
            end
        end
    end
    return ''
end

-------------------------------------------------------------------------------------
-- [读取] 获取主线分支对应数据[任务名称对应的资源,分支名称,任务IDX]
quest_res.get_bran_info_res = function(g_task_res,bran_name,task_idx)
    local info = g_task_res
    if not table.is_empty(info) then
        local bran_info = info[bran_name..task_idx] or info[bran_name] or {}
        return bran_info
    end
    return {}
end

-------------------------------------------------------------------------------------
-- [读取] 排序任务分支,返回排序后任务分支
quest_res.get_sort_bran_list = function(g_task_res,bran_list,task_idx)
    -- 保存返回数据
    local r_bran_list = {}
    -- 遍历游戏分支数据
    for _,v1 in pairs(bran_list) do
        -- 分支名称
        local bran_name = v1.branch_name
        -- 分支对应的资源
        local bran_info   = this.get_bran_info_res(g_task_res,bran_name,task_idx)
    end
end

-------------------------------------------------------------------------------------
-- 返回对象
return quest_res

